import React, { useState } from "react";
import "./ItemCount.css"



function ItemCount() {
    

    const stock = 25

    if (stock < 25){
        const [count, setCount] = useState(1);
        const increment = () => {
            setCount(count + 1);
        }
          
        const decrement = () => {
            setCount(count - 1);
        }
    }else {
        console.log("No hay màs Stock")
    }
    
    

    
 

    return (
        <main id="producto">
            <b>Producto</b>
            <section className="Counters">
                <div className="counter">
                    <p className="count">{count}</p>
                    <section className="controls">
                        <button onClick={increment}>Agregar otro producto</button>
                        <button onClick={decrement}>Quitar un producto</button>
                        <button onClick={() => setCount(0)}>Borrar</button>
                        <button>Enviar al Carrito</button>
                    </section>

                </div>

            </section>
        </main>
    );
    
    
}


export default ItemCount;
